changeSet.sql:
CREATE TABLE metadata.science_keyword
(
id uuid NOT NULL,
version integer DEFAULT 1,
category text NOT NULL,
topic text NOT NULL,
term text,
variable_level_1 text,
variable_level_2 text,
variable_level_3 text,
variable_level_4 text,
display text,
date_created timestamp with time zone DEFAULT default_timestamp(),
date_updated timestamp with time zone DEFAULT default_timestamp(),

CONSTRAINT pkey_science_keyword_id PRIMARY KEY (id)
)
WITH (
OIDS=FALSE
);

ALTER TABLE metadata.science_keyword OWNER TO "${database.role.admin}";
REVOKE ALL ON TABLE metadata.science_keyword FROM public;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE metadata.science_keyword TO "${database.role.user}";
GRANT SELECT ON TABLE metadata.science_keyword TO "${database.role.read_only}";

