CREATE TABLE metadata.descriptive_metadata_science_keyword_xref
(
	descriptive_metadata_id uuid NOT NULL,
	science_keyword_id uuid NOT NULL,
	CONSTRAINT pkey_descriptive_metadata_science_keyword_xref PRIMARY KEY (descriptive_metadata_id, science_keyword_id),
	CONSTRAINT fkey_metadata_science_keyword_xref_descriptive_metadata FOREIGN KEY (descriptive_metadata_id)
		REFERENCES metadata.descriptive_metadata (id) MATCH SIMPLE
		ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,
	CONSTRAINT fkey_descriptive_metadata_science_keyword_xref_science_keyword FOREIGN KEY (science_keyword_id)
		REFERENCES metadata.science_keyword (id) MATCH SIMPLE
		ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED
)
WITH (
	OIDS=FALSE
);

ALTER TABLE metadata.descriptive_metadata_science_keyword_xref OWNER TO "${database.role.admin}";
GRANT ALL ON TABLE metadata.descriptive_metadata_science_keyword_xref TO "${database.role.admin}";
GRANT SELECT, UPDATE, INSERT, DELETE ON TABLE metadata.descriptive_metadata_science_keyword_xref TO "${database.role.user}";
GRANT SELECT ON TABLE metadata.descriptive_metadata_science_keyword_xref TO "${database.role.read_only}";

CREATE INDEX idx_d_m_s_k_xref_descriptive_metadata_id
ON metadata.descriptive_metadata_science_keyword_xref
USING btree
(descriptive_metadata_id);

CREATE INDEX idx_d_m_s_k_xref_science_keyword_id
ON metadata.descriptive_metadata_science_keyword_xref
USING btree
(science_keyword_id);


DROP TRIGGER IF EXISTS trigger_science_keyword_display ON metadata.science_keyword;

CREATE TRIGGER trigger_science_keyword_display 
BEFORE INSERT OR UPDATE ON metadata.science_keyword
FOR EACH ROW 
EXECUTE PROCEDURE metadata.update_science_keyword_display();
