changeSet.sql:
CREATE TABLE metadata.instrument_keyword
(
id uuid NOT NULL,
version integer DEFAULT 1,
category text NOT NULL,
class text,
type text,
subtype text,
short_name text,
long_name text,
display text,
date_created timestamp with time zone DEFAULT default_timestamp(),
date_updated timestamp with time zone DEFAULT default_timestamp(),
CONSTRAINT pkey_instrument_keyword_id PRIMARY KEY (id)
)
WITH (
OIDS=FALSE
);

ALTER TABLE metadata.instrument_keyword OWNER TO "${database.role.admin}";
REVOKE ALL ON TABLE metadata.instrument_keyword FROM public;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE metadata.instrument_keyword TO "${database.role.user}";
GRANT SELECT ON TABLE metadata.instrument_keyword TO "${database.role.read_only}";

