CREATE TABLE metadata.descriptive_metadata_instrument_keyword_xref
(
	descriptive_metadata_id uuid NOT NULL,
	instrument_keyword_id uuid NOT NULL,
	CONSTRAINT pkey_descriptive_metadata_instrument_keyword_xref PRIMARY KEY (descriptive_metadata_id, instrument_keyword_id),
	CONSTRAINT fkey_metadata_instrument_keyword_xref_descriptive_metadata FOREIGN KEY (descriptive_metadata_id)
		REFERENCES metadata.descriptive_metadata (id) MATCH SIMPLE
		ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,
	CONSTRAINT fkey_descr_metadata_instrument_keyword_xref_instrument_keyword FOREIGN KEY (instrument_keyword_id)
		REFERENCES metadata.instrument_keyword (id) MATCH SIMPLE
		ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED
)
WITH (
	OIDS=FALSE
);

ALTER TABLE metadata.descriptive_metadata_instrument_keyword_xref OWNER TO "${database.role.admin}";
GRANT ALL ON TABLE metadata.descriptive_metadata_instrument_keyword_xref TO "${database.role.admin}";
GRANT SELECT, UPDATE, INSERT, DELETE ON TABLE metadata.descriptive_metadata_instrument_keyword_xref TO "${database.role.user}";
GRANT SELECT ON TABLE metadata.descriptive_metadata_instrument_keyword_xref TO "${database.role.read_only}";

CREATE INDEX idx_d_m_i_k_xref_descriptive_metadata_id
ON metadata.descriptive_metadata_instrument_keyword_xref
USING btree
(descriptive_metadata_id);

CREATE INDEX idx_d_m_i_k_xref_instrument_keyword_id
ON metadata.descriptive_metadata_instrument_keyword_xref
USING btree
(instrument_keyword_id);

UPDATE metadata.instrument_type set short_name = 'SSMI' where short_name = 'SSM/I';
UPDATE metadata.instrument_type set short_name = 'Infrared Thermometers' where short_name = 'Infraded Thermometers';
UPDATE metadata.instrument_type set short_name = 'SPACE RADIATION DOSIMETER' where short_name = 'Gamma Radiation Detector';

INSERT INTO metadata.descriptive_metadata_instrument_keyword_xref (descriptive_metadata_id, instrument_keyword_id)
	(SELECT xref.descriptive_metadata_id, xref.instrument_type_id 
	FROM metadata.descriptive_metadata_instrument_type_xref xref
	where instrument_type_id != (
		SELECT id from metadata.instrument_type where short_name = 'Other')
	);


DROP TRIGGER IF EXISTS trigger_instrument_keyword_display ON metadata.instrument_keyword;

CREATE TRIGGER trigger_instrument_keyword_display 
BEFORE INSERT OR UPDATE ON metadata.instrument_keyword
FOR EACH ROW 
EXECUTE PROCEDURE metadata.update_instrument_keyword_display();

DELETE FROM metadata.descriptive_metadata_instrument_keyword_xref WHERE instrument_keyword_id = 
	(SELECT id FROM metadata.instrument_keyword
	WHERE short_name = 'Gamma Radiation Detector');
